import { SxProps, Theme } from '@mui/material';

type Key = 'sectionContainer' | 'name' | 'workTitle' | 'description' | 'avatarContainer' | 'avatar' | 'link' | 'linksContainer';

export const styles: Record<Key, SxProps<Theme>> = {
  sectionContainer: (theme: Theme) => ({
    backgroundColor: theme.palette.background.default,
    textAlign: 'left',
    display: 'flex',
    flexDirection: { xs: 'column', sm: 'row-reverse', md: 'column' },
    justifyContent: 'flex-start',
    alignItems: 'center',
    height: '100%',
  }),
  avatarContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  avatar: {
    minWidth: '150px',
    minHeight: '150px',
    margin: '20px 80px 40px',
  },
  name: (theme: Theme) => ({
    padding: '0.5rem 0',
    textAlign: 'left',
    fontSize: '3.25rem !important',
    fontWeight: 'bold',
    color: theme.palette.text.disabled,
  }),
  description: (theme: Theme) => ({
    padding: '0.5rem 0',
    textAlign: 'left',
    color: theme.palette.text.secondary,
  }),
  workTitle: (theme: Theme) => ({
    padding: '0.5rem 0',
    textAlign: 'left',
    color: theme.palette.text.primary,
  }),
  linksContainer: {
    display: 'flex',
    gap: '1rem'
  },
  link: (theme: Theme) => ({
    height: '40px',
    width: '40px',
    padding: '0px',
    margin: '22px 0',
    'a svg': {
      fill: theme.palette.text.secondary,
      '&:hover': {
        fill: theme.palette.text.disabled,
      }
    },
  }),
};
