import { SxProps, Theme } from '@mui/material';

type Key = 'pill' | 'label';

export const styles: Record<Key, SxProps<Theme>> = {
  pill: (theme: Theme) => ({
    backgroundColor: theme.palette.background.default,
    height: '100%',
    display: 'flex',
    flexDirection: { xs: 'column', sm: 'row' },
  }),
  label: (theme: Theme) => ({
    backgroundColor: theme.palette.background.default,
    height: '100%',
    display: 'flex',
    flexDirection: { xs: 'column', sm: 'row' },
  }),
};
