import { SxProps, Theme } from '@mui/material';

type Key = 'pill' | 'label';

export const styles: Record<Key, SxProps<Theme>> = {
  pill: (theme: Theme) => ({
    backgroundColor: theme.palette.text.primary,
    opacity: '0.2',
    borderRadius: '',
  }),
  label: (theme: Theme) => ({
    color: theme.palette.text.disabled,
  }),
};
